mapper.select:
SELECT bbs_no, bbs_title, bbs_content, bbs_writer, bbs_reg_date, bbs_count
FROM bbs
WHERE bbs_no = #{bbsNo}

mapper.update:
UPDATE bbs
SET bbs_title = #{bbsTitle}, bbs_content = #{bbsContent}
WHERE bbs_no = #{bbsNo} AND bbs_writer = #{bbsWriter}

mapper.delete:
DELETE
FROM bbs
WHERE bbs_no = #{bbsNo} AND bbs_writer = #{bbsWriter}
